import React, { useContext, useEffect, useState } from 'react';
import { GlobalContext } from '../context/GlobalContext';
import useIndexedDB from '../hooks/useIndexedDB';
import '../styles/contactList.css';

const ContactList = () => {
    const {
        contacts,
        dispatchContacts,
        selectedContact,
        setSelectedContact
    } = useContext(GlobalContext);

    const {
        getFromDB,
        addToDB,
        deleteFromDB,
        updateInDB
    } = useIndexedDB();

    const [newContactName, setNewContactName] = useState('');
    const [newContactPic, setNewContactPic] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [showOptions, setShowOptions] = useState(false);
    const [optionsPosition, setOptionsPosition] = useState({ top: 0, left: 0 }); // Position for options menu
    const [searchTerm, setSearchTerm] = useState(''); // State for search term

    useEffect(() => {
        const fetchContacts = async () => {
            try {
                const storedContacts = await getFromDB('contacts');
                if (storedContacts.length) {
                    dispatchContacts({ type: 'SET_CONTACTS', payload: storedContacts });
                } else {
                    const contactData = [
                        { id: 1, name: 'Garvita', pic: '' },
                        { id: 2, name: 'Heena', pic: '' },
                        { id: 3, name: 'Sayani', pic: '' }
                    ];
                    contactData.forEach((contact) => addToDB('contacts', contact));
                    dispatchContacts({ type: 'SET_CONTACTS', payload: contactData });
                }
            } catch (error) {
                console.error('Error fetching contacts:', error);
            }
        };
        fetchContacts();
    }, [dispatchContacts, getFromDB, addToDB]);

    // Filtered contacts based on search term
    const filteredContacts = contacts.filter((contact) =>
        contact.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Add a new contact
    const handleAddContact = async () => {
        if (newContactName.trim()) {
            const newContact = { id: Date.now(), name: newContactName, pic: newContactPic || '' };
            await addToDB('contacts', newContact);
            dispatchContacts({ type: 'ADD_CONTACT', payload: newContact });
            setNewContactName('');
            setNewContactPic('');
            setShowForm(false);
        }
    };

    // Handle profile click (to show options)
    const handleProfileClick = (event, contact) => {
        setSelectedContact(contact.id);
        const { top, left, width, height } = event.target.getBoundingClientRect();
        setOptionsPosition({ top: top + height, left: left + width / 2 }); // Positioning the options menu
        setShowOptions(true);
    };

    // Block contact
    const handleBlockContact = (id) => {
        console.log(`Contact with ID ${id} is blocked.`);
        setShowOptions(false);
    };

    // Edit contact
    const handleEditContact = (id) => {
        const contactToEdit = contacts.find((contact) => contact.id === id);
        if (contactToEdit) {
            setNewContactName(contactToEdit.name);
            setShowForm(true);
        }
        setShowOptions(false);
    };

    // Delete a contact
    const handleDeleteContact = async (id) => {
        await deleteFromDB('contacts', id);
        dispatchContacts({ type: 'DELETE_CONTACT', payload: id });
        setShowOptions(false);
    };

    // Handle updating contact
    const handleUpdateContact = () => {
        const updatedContact = { id: selectedContact, name: newContactName, pic: newContactPic || '' };
        updateInDB('contacts', updatedContact);
        dispatchContacts({ type: 'UPDATE_CONTACT', payload: updatedContact });
        setShowForm(false);
    };

    // Toggle Theme (Emoji Click)
    const toggleTheme = () => {
        document.body.classList.toggle('dark');
    };

    return (
        <div className="contact-list">
            {/* Search Bar */}
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Search Contacts"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>

            {/* Contact List Header */}
            <div className="contact-list-header">
                <h2>
                    Contacts
                    <span className="add-contact-icon" onClick={() => setShowForm(!showForm)}>
                        âž•
                    </span>
                    <span className="theme-toggle" onClick={toggleTheme}>
                        ðŸŒ—
                    </span>
                </h2>
            </div>

            {/* Add/Edit Contact Form */}
            {showForm && (
                <div className="add-contact-form">
                    <input
                        type="text"
                        placeholder="Enter contact name"
                        value={newContactName}
                        onChange={(e) => setNewContactName(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Enter profile picture URL (optional)"
                        value={newContactPic}
                        onChange={(e) => setNewContactPic(e.target.value)}
                    />
                    <button onClick={selectedContact ? handleUpdateContact : handleAddContact}>
                        {selectedContact ? 'Update Contact' : 'Add Contact'}
                    </button>
                </div>
            )}

            {/* Contact List */}
            {filteredContacts.map((contact) => (
                <div
                    key={contact.id}
                    className={`contact-item ${selectedContact === contact.id ? 'selected' : ''}`}
                    onClick={() => setSelectedContact(contact.id)}
                >
                    <div className="contact-pic-container" onClick={(e) => handleProfileClick(e, contact)}>
                        {contact.pic ? (
                            <img
                                src={contact.pic}
                                alt={contact.name}
                                className="contact-pic"
                            />
                        ) : (
                            <div className="contact-initial">{contact.name[0].toUpperCase()}</div>
                        )}
                    </div>

                    <div className="contact-info">
                        <span className="contact-name">{contact.name}</span>
                    </div>
                </div>
            ))}

            {/* Contact Options Menu */}
            {showOptions && selectedContact && (
                <div
                    className="contact-options"
                    style={{ top: `${optionsPosition.top}px`, left: `${optionsPosition.left}px` }}
                >
                    <button onClick={() => handleBlockContact(selectedContact)}>Block</button>
                    <button onClick={() => handleEditContact(selectedContact)}>Edit Contact</button>
                    <button onClick={() => handleDeleteContact(selectedContact)}>Delete</button>
                </div>
            )}
        </div>
    );
};

export default ContactList;
