import React, { useContext, useEffect, useState } from 'react';
import { GlobalContext } from '../context/GlobalContext';
import useIndexedDB from '../hooks/useIndexedDB';
import '../styles/contactList.css';

const ContactList = () => {
    const { contacts, dispatchContacts, selectedContact, setSelectedContact } = useContext(GlobalContext);
    const { getFromDB, addToDB, deleteFromDB, updateInDB } = useIndexedDB(); // Added updateInDB
    const [newContactName, setNewContactName] = useState('');
    const [newContactPic, setNewContactPic] = useState('');
    const [showForm, setShowForm] = useState(false); // Toggle for add contact form
    const [showOptions, setShowOptions] = useState(false); // To show contact options
    const [showArchived, setShowArchived] = useState(false); // Toggle archived section
    const [archivedContacts, setArchivedContacts] = useState([]); // State for archived contacts
    const [theme, setTheme] = useState('light'); // Default theme
    const [favorites, setFavorites] = useState([]); // State for favorites

    // Fetch contacts from IndexedDB or static data
    useEffect(() => {
        const fetchContacts = async () => {
            try {
                const storedContacts = await getFromDB('contacts');
                if (storedContacts.length) {
                    dispatchContacts({ type: 'SET_CONTACTS', payload: storedContacts });
                } else {
                    const contactData = [
                        { id: 1, name: 'Alice', pic: '' },
                        { id: 2, name: 'Bob', pic: '' },
                    ];
                    contactData.forEach((contact) => addToDB('contacts', contact));
                    dispatchContacts({ type: 'SET_CONTACTS', payload: contactData });
                }
            } catch (error) {
                console.error('Error fetching contacts:', error);
            }
        };

        fetchContacts();
    }, [dispatchContacts, getFromDB, addToDB]);

    // Add a new contact
    const handleAddContact = async () => {
        if (newContactName.trim()) {
            const newContact = {
                id: Date.now(),
                name: newContactName,
                pic: newContactPic || '', // Optional profile picture
            };
            await addToDB('contacts', newContact);
            dispatchContacts({ type: 'ADD_CONTACT', payload: newContact });
            setNewContactName('');
            setNewContactPic('');
            setShowForm(false); // Close the form
        }
    };

    // Delete a contact
    const handleDeleteContact = async (id) => {
        await deleteFromDB('contacts', id);
        dispatchContacts({ type: 'DELETE_CONTACT', payload: id });
    };

    // Archive contact
    const handleArchiveContact = (contact) => {
        setArchivedContacts((prev) => [...prev, contact]); // Add to archived contacts
        handleDeleteContact(contact.id); // Remove from the active contacts list
    };

    // Show contact options
    const handleProfileClick = (contact) => {
        setSelectedContact(contact.id);
        setShowOptions(true);
    };

    // Block contact (add your own logic)
    const handleBlockContact = (id) => {
        console.log(`Contact with ID ${id} is blocked.`);
        setShowOptions(false);
    };

    // Edit contact (opens form to edit name)
    const handleEditContact = (id) => {
        const contactToEdit = contacts.find((contact) => contact.id === id);
        if (contactToEdit) {
            setNewContactName(contactToEdit.name);
            setShowForm(true); // Show the form to edit contact
        }
        setShowOptions(false);
    };

    // Update the contact's name
    const handleUpdateContact = async () => {
        if (newContactName.trim() && selectedContact) {
            const updatedContact = {
                id: selectedContact,
                name: newContactName,
                pic: newContactPic || '',
            };
            await updateInDB('contacts', updatedContact); // Update contact in IndexedDB
            dispatchContacts({ type: 'UPDATE_CONTACT', payload: updatedContact }); // Update global state or UI
            setNewContactName('');
            setNewContactPic('');
            setShowForm(false); // Close the form
        }
    };


    // Add to favorites
    const handleAddToFavorites = (id) => {
        const contactToAdd = contacts.find((contact) => contact.id === id);
        if (contactToAdd) {
            setFavorites((prev) => [...prev, contactToAdd]);
        }
        setShowOptions(false);
    };

    // Toggle Theme (Emoji Click)
    const toggleTheme = () => {
        const newTheme = theme === 'light' ? 'dark' : 'light';
        setTheme(newTheme);
        document.body.className = newTheme; // Apply theme to the body
    };

    return (
        <div className={`contact-list ${theme}`}>
            {/* Archived Contacts Section */}
            {showArchived && (
                <div className="archived-contacts">
                    <h3>Archived Contacts</h3>
                    <div id="archivedItems">
                        {archivedContacts.map((contact) => (
                            <div key={contact.id} className="contact-item">
                                <div className="contact-pic-container">
                                    {contact.pic ? (
                                        <img src={contact.pic} alt={contact.name} className="contact-pic" />
                                    ) : (
                                        <div className="contact-initial">
                                            {contact.name[0].toUpperCase()}
                                        </div>
                                    )}
                                </div>
                                <div className="contact-info">
                                    <span className="contact-name">{contact.name}</span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            {/* Contact List Header */}
            <div className="contact-list-header">
                <h2>
                    Contacts
                    <span className="add-contact-icon" onClick={() => setShowForm(!showForm)}>
                        ‚ûï
                    </span>
                    <span className="theme-toggle" onClick={toggleTheme}>
                        üåó
                    </span>
                </h2>

                <div className="archive-toggle-container">
                    <button onClick={() => setShowArchived(!showArchived)}>
                        {showArchived ? 'Hide Archived' : 'Show Archived'}
                    </button>
                </div>
            </div>

            {/* Add/Edit Contact Form */}
            {showForm && (
                <div className="add-contact-form">
                    <input
                        type="text"
                        placeholder="Enter contact name"
                        value={newContactName}
                        onChange={(e) => setNewContactName(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Enter profile picture URL (optional)"
                        value={newContactPic}
                        onChange={(e) => setNewContactPic(e.target.value)}
                    />
                    {selectedContact ? (
                        <button onClick={handleUpdateContact}>Update Contact</button>
                    ) : (
                        <button onClick={handleAddContact}>Add Contact</button>
                    )}
                </div>
            )}

            {/* Contact List */}
            {contacts.map((contact) => (
                <div
                    key={contact.id}
                    className={`contact-item ${selectedContact === contact.id ? 'selected' : ''}`}
                    onClick={() => setSelectedContact(contact.id)}
                >
                    <div className="contact-pic-container" onClick={() => handleProfileClick(contact)}>
                        {contact.pic ? (
                            <img src={contact.pic} alt={contact.name} className="contact-pic" />
                        ) : (
                            <div className="contact-initial">
                                {contact.name[0].toUpperCase()}
                            </div>
                        )}
                    </div>
                    <div className="contact-info">
                        <span className="contact-name">{contact.name}</span>

                    </div>
                    {favorites.some((fav) => fav.id === contact.id) && (
                        <span className="favorite-icon">‚ù§Ô∏è</span>
                    )}
                    <button onClick={() => handleArchiveContact(contact)}>Archive</button>
                </div>
            ))}

            {/* Contact Options Menu */}
            {showOptions && selectedContact && (
                <div className="contact-options">
                    <button onClick={() => handleBlockContact(selectedContact)}>Block</button>
                    <button onClick={() => handleEditContact(selectedContact)}>Edit Contact</button>
                    <button onClick={() => handleAddToFavorites(selectedContact)}>Add to Favorites</button>
                    <button onClick={() => handleDeleteContact(selectedContact)}>Delete</button>
                </div>
            )}
        </div>
    );
};

export default ContactList;
