import React, { useContext, useState } from 'react';
import { GlobalContext } from '../context/GlobalContext';
import useIndexedDB from '../hooks/useIndexedDB';
import '../styles/messageInput.css';

const emojis = [
    "ðŸ˜€", "ðŸ˜", "ðŸ˜‚", "ðŸ¤£", "ðŸ˜ƒ", "ðŸ˜„", "ðŸ˜…", "ðŸ˜†", "ðŸ˜‰", "ðŸ˜Š",
    "ðŸ˜‹", "ðŸ˜Ž", "ðŸ˜", "ðŸ˜˜", "ðŸ˜—", "ðŸ˜™", "ðŸ˜š", "ðŸ™‚", "ðŸ¤—", "ðŸ¤”",
    "ðŸ˜", "ðŸ˜‘", "ðŸ˜¶", "ðŸ™„", "ðŸ˜", "ðŸ˜£", "ðŸ˜¥", "ðŸ˜®", "ðŸ¤", "ðŸ˜¯",
    // Add more emojis as needed
];

const MessageInput = () => {
    const [input, setInput] = useState('');
    const [emojiPickerVisible, setEmojiPickerVisible] = useState(false);
    const [attachmentMenuVisible, setAttachmentMenuVisible] = useState(false);
    const { dispatchMessages, selectedContact } = useContext(GlobalContext);
    const { addToDB } = useIndexedDB();

    const handleSend = async () => {
        if (!selectedContact) return;

        const newMessage = {
            id: Date.now(),
            text: input,
            sender: 'me',
            timestamp: new Date().toLocaleTimeString(),
            contactId: selectedContact,
        };

        try {
            await addToDB('messages', newMessage);
            dispatchMessages({ type: 'ADD_MESSAGE', payload: newMessage });
            setInput('');
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    const handleEmojiClick = (emoji) => {
        setInput(input + emoji);
        setEmojiPickerVisible(false);
    };

    const handleClickOutside = (e) => {
        if (!e.target.closest('.emoji-picker') && !e.target.closest('.attachment-menu')) {
            setEmojiPickerVisible(false);
            setAttachmentMenuVisible(false);
        }
    };

    React.useEffect(() => {
        document.addEventListener('click', handleClickOutside);
        return () => {
            document.removeEventListener('click', handleClickOutside);
        };
    }, []);

    return (
        <div className="message-input-container">
            <div className="attachment-menu" style={{ display: attachmentMenuVisible ? 'block' : 'none' }}>
                <button onClick={() => alert('Upload Image/Video')}>ðŸ“· Upload</button>
                <button onClick={() => alert('Share Contact')}>ðŸ“‡ Share Contact</button>
            </div>
            <div className="emoji-picker" style={{ display: emojiPickerVisible ? 'block' : 'none' }}>
                <div className="emoji-search">Search emojis...</div>
                <div className="emoji-list">
                    {emojis.map((emoji, index) => (
                        <span key={index} onClick={() => handleEmojiClick(emoji)}>{emoji}</span>
                    ))}
                </div>
            </div>
            <button className="attachment-button" onClick={() => setAttachmentMenuVisible(!attachmentMenuVisible)}>ðŸ“Ž</button>
            <button className="emoji-button" onClick={() => setEmojiPickerVisible(!emojiPickerVisible)}>ðŸ˜Š</button>
            <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type a message"
            />
            <button onClick={handleSend}>Send</button>
            <button className="voice-note-button" onClick={() => alert('Record voice note')}>ðŸŽ¤</button>
        </div>
    );
};

export default MessageInput;
