import React, { useContext, useState } from "react";
import { GlobalContext } from "../context/GlobalContext";
import "../styles/messageInput.css";

const MessageInput = () => {
    const [text, setText] = useState(""); // Input text state
    const { dispatchMessages } = useContext(GlobalContext); // Access dispatch function

    const handleSendMessage = () => {
        if (text.trim() !== "") {
            const newMessage = {
                id: Date.now(), // Unique ID
                text, // Message text
                isReceived: false, // Set to false for sent messages
            };
            dispatchMessages({ type: "ADD_MESSAGE", payload: newMessage });
            setText(""); // Clear the input field
        }
    };

    return (
        <div className="message-input">
            <input
                type="text"
                placeholder="Type your message..."
                value={text}
                onChange={(e) => setText(e.target.value)}
            />
            <button onClick={handleSendMessage}>Send</button>
        </div>
    );
};

export default MessageInput;
