import React, { useContext, useEffect, useRef, useState } from 'react';
import { GlobalContext } from '../context/GlobalContext';
import useIndexedDB from '../hooks/useIndexedDB';
import '../styles/messageInput.css';

const emojis = [
    '😀', '😃', '😄', '😁', '😆', '😅', '😂', '🤣', '😊', '😇',
    '🙂', '🙃', '😉', '😌', '😍', '🥰', '😘', '😗', '😙', '😚',
    '😋', '😜', '🤪', '😝', '🤑', '🤗', '🤔', '🤭', '🤫', '🤥',
    '😶', '😐', '😑', '😏', '😒', '🙄', '😬', '🤥', '😪', '😴',
    '😷', '🤒', '🤕', '🤢', '🤮', '🤧', '🥵', '🥶', '😵', '🤯',
    '😎', '🤓', '🧐', '😕', '😟', '🙁', '☹️', '😮', '😯', '😲',
    '😳', '🥺', '😢', '😭', '😤', '😠', '😡', '🤬', '🤯', '😱',
    '😨', '😰', '😥', '😓', '🤩', '😵‍💫', '🥳', '😏', '😒', '🙃',
    '🙈', '🙉', '🙊', '🐵', '🐒', '🦍', '🐶', '🐕', '🦮', '🐕‍🦺',
    '🐩', '🐺', '🦊', '🦝', '🐱', '🐈', '🐈‍⬛', '🦁', '🐯', '🐅',
    '🐆', '🐴', '🐎', '🦄', '🐮', '🐂', '🐃', '🐄', '🐷', '🐖',
    '🐗', '🐽', '🐏', '🐑', '🐐', '🐪', '🐫', '🦙', '🦒', '🦏'
];

const mockContacts = Array.from({ length: 50 }, (_, i) => ({
    id: i + 1,
    name: `Contact ${i + 1}`,
    number: `+12345678${i.toString().padStart(2, '0')}`,
}));

const MessageInput = () => {
    const [input, setInput] = useState('');
    const [showAttachmentMenu, setShowAttachmentMenu] = useState(false);
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const [showContactPicker, setShowContactPicker] = useState(false);
    const { dispatchMessages, selectedContact } = useContext(GlobalContext);
    const { addToDB } = useIndexedDB();
    const attachmentMenuRef = useRef(null);

    const handleSend = async () => {
        if (!selectedContact || !input) return;

        const newMessage = {
            id: Date.now(),
            text: input,
            sender: 'me',
            timestamp: new Date().toLocaleTimeString(),
            contactId: selectedContact,
        };

        try {
            await addToDB('messages', newMessage);
            dispatchMessages({ type: 'ADD_MESSAGE', payload: newMessage });
            setInput('');
            setShowAttachmentMenu(false);
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    const handleEmojiClick = (emoji) => {
        setInput((prev) => prev + emoji);
        setShowEmojiPicker(false); // Close emoji picker after selecting an emoji
    };

    const handleContactClick = (contact) => {
        setInput((prev) => `${prev} [${contact.name}: ${contact.number}]`);
        setShowContactPicker(false); // Close contact picker after selection
    };

    // Close menus when clicking outside
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (
                attachmentMenuRef.current &&
                !attachmentMenuRef.current.contains(event.target)
            ) {
                setShowAttachmentMenu(false);
                setShowEmojiPicker(false);
                setShowContactPicker(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    return (
        <div className="message-input">
            <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type a message"
            />
            {/* Attachment button */}
            <button
                className="attachment-btn"
                onClick={() => setShowAttachmentMenu((prev) => !prev)}
            >
                📎
            </button>

            {showAttachmentMenu && (
                <div className="attachment-menu" ref={attachmentMenuRef}>
                    <button onClick={() => setShowEmojiPicker((prev) => !prev)}>
                        😊 Emojis
                    </button>
                    <button onClick={() => setShowContactPicker((prev) => !prev)}>
                        📇 Share Contact
                    </button>
                    <input
                        type="file"
                        accept="image/*,video/*"
                        style={{ display: 'none' }}
                        id="file-input"
                    />
                    <label htmlFor="file-input">
                        <button>📷 Upload</button>
                    </label>
                </div>
            )}

            {showEmojiPicker && (
                <div className="emoji-picker" ref={attachmentMenuRef}>
                    <h4>Emojis</h4>
                    <div
                        className="emoji-grid"
                        style={{
                            maxHeight: '200px',
                            maxWidth: '400px',
                            overflowY: 'auto',
                            padding: '5px',
                        }}
                    >
                        {emojis.map((emoji) => (
                            <button
                                key={emoji}
                                onClick={() => handleEmojiClick(emoji)}
                                style={{
                                    background: 'none',
                                    border: 'none',
                                    cursor: 'pointer',
                                    fontSize: '20px',
                                    margin: '5px',
                                }}
                            >
                                {emoji}
                            </button>
                        ))}
                    </div>
                </div>
            )}

            {showContactPicker && (
                <div
                    className="contact-picker"
                    ref={attachmentMenuRef}
                    style={{
                        maxHeight: '200px',
                        maxWidth: '300px',
                        overflowY: 'auto',
                        background: '#f9f9f9',
                        border: '1px solid #ccc',
                        borderRadius: '5px',
                        padding: '10px',
                        position: 'absolute',
                        zIndex: '100',
                    }}
                >
                    <h4>Select a Contact</h4>
                    {mockContacts.map((contact) => (
                        <div
                            key={contact.id}
                            onClick={() => handleContactClick(contact)}
                            style={{
                                cursor: 'pointer',
                                padding: '5px',
                                borderBottom: '1px solid #eee',
                            }}
                        >
                            {contact.name} - {contact.number}
                        </div>
                    ))}
                </div>
            )}

            <button onClick={handleSend}>Send</button>
        </div>
    );
};

export default MessageInput;
