import React, { useContext, useState } from 'react';
import { GlobalContext } from '../context/GlobalContext';
import useIndexedDB from '../hooks/useIndexedDB';
import '../styles/messageInput.css';

const emojis = [
    'ðŸ˜€', 'ðŸ˜ƒ', 'ðŸ˜„', 'ðŸ˜', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜‚', 'ðŸ¤£', 'ðŸ˜Š', 'ðŸ˜‡',
    'ðŸ™‚', 'ðŸ™ƒ', 'ðŸ˜‰', 'ðŸ˜Œ', 'ðŸ˜', 'ðŸ¥°', 'ðŸ˜˜', 'ðŸ˜—', 'ðŸ˜™', 'ðŸ˜š',
    'ðŸ˜‹', 'ðŸ˜œ', 'ðŸ¤ª', 'ðŸ˜', 'ðŸ¤‘', 'ðŸ¤—', 'ðŸ¤”', 'ðŸ¤­', 'ðŸ¤«', 'ðŸ¤¥',
    'ðŸ˜¶', 'ðŸ˜', 'ðŸ˜‘', 'ðŸ˜', 'ðŸ˜’', 'ðŸ™„', 'ðŸ˜¬', 'ðŸ¤¥', 'ðŸ˜ª', 'ðŸ˜´',
    'ðŸ˜·', 'ðŸ¤’', 'ðŸ¤•', 'ðŸ¤¢', 'ðŸ¤®', 'ðŸ¤§', 'ðŸ¥µ', 'ðŸ¥¶', 'ðŸ˜µ', 'ðŸ¤¯',
];

const MessageInput = () => {
    const [input, setInput] = useState('');
    const [media, setMedia] = useState(null);
    const [showOptions, setShowOptions] = useState(false); // Toggle attachment options
    const [showEmojis, setShowEmojis] = useState(false);  // Toggle emoji picker
    const { dispatchMessages, selectedContact } = useContext(GlobalContext);
    const { addToDB } = useIndexedDB();

    const handleSend = async () => {
        if (!selectedContact || (!input && !media)) return;

        const newMessage = {
            id: Date.now(),
            text: input,
            media,
            sender: 'me',
            timestamp: new Date().toLocaleTimeString(),
            contactId: selectedContact,
        };

        try {
            await addToDB('messages', newMessage);
            dispatchMessages({ type: 'ADD_MESSAGE', payload: newMessage });
            setInput('');
            setMedia(null);
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setMedia(URL.createObjectURL(file));
        }
    };

    const handleEmojiClick = (emoji) => {
        setInput((prev) => prev + emoji);
        setShowEmojis(false); // Close the emoji picker after selection
    };

    const handleContactShare = () => {
        const contactInfo = {
            name: 'John Doe',
            phone: '123-456-7890',
        };

        const newMessage = {
            id: Date.now(),
            text: `Contact shared: ${contactInfo.name} - ${contactInfo.phone}`,
            sender: 'me',
            timestamp: new Date().toLocaleTimeString(),
            contactId: selectedContact,
        };

        try {
            addToDB('messages', newMessage);
            dispatchMessages({ type: 'ADD_MESSAGE', payload: newMessage });
        } catch (error) {
            console.error('Error sharing contact:', error);
        }
    };

    return (
        <div className="message-input">
            <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type a message"
            />
            <button onClick={() => setShowOptions((prev) => !prev)}>ðŸ“Ž</button> {/* Toggle options */}
            {showOptions && (
                <div className="attachment-options">
                    <input
                        type="file"
                        accept="image/*,video/*"
                        onChange={handleFileChange}
                        style={{ display: 'none' }}
                        id="media-input"
                    />
                    <label htmlFor="media-input">
                        <button>ðŸ“· Image/Video</button>
                    </label>
                    <button onClick={() => setShowEmojis((prev) => !prev)}>ðŸ˜Š Emojis</button>
                    <button onClick={handleContactShare}>ðŸ“‡ Share Contact</button>
                </div>
            )}
            {showEmojis && (
                <div className="emoji-picker">
                    {emojis.map((emoji) => (
                        <button
                            key={emoji}
                            onClick={() => handleEmojiClick(emoji)}
                            style={{ fontSize: '20px', margin: '5px' }}
                        >
                            {emoji}
                        </button>
                    ))}
                </div>
            )}
            <button onClick={handleSend}>Send</button>
        </div>
    );
};

export default MessageInput;
