import React, { createContext, useReducer } from "react";
import contactReducer from "../reducers/contactReducer";
import messageReducer from "../reducers/messageReducer";

export const GlobalContext = createContext();

const initialState = {
    contacts: [],
    messages: [], // Ensure this is defined properly
};

const GlobalProvider = ({ children }) => {
    const [contacts, dispatchContacts] = useReducer(contactReducer, initialState.contacts);
    const [messages, dispatchMessages] = useReducer(messageReducer, initialState.messages);

    return (
        <GlobalContext.Provider
            value={{
                contacts,
                messages,
                dispatchContacts,
                dispatchMessages,
            }}
        >
            {children}
        </GlobalContext.Provider>
    );
};


export default GlobalProvider;
