import React, { useContext } from 'react';
import { GlobalContext } from '../context/GlobalContext';
import '../styles/chatWindow.css';
import Message from './Message';
import MessageInput from './MessageInput';

const ChatWindow = () => {
    const { messages, selectedContact } = useContext(GlobalContext);

    // Get current theme from body class
    const currentTheme = document.body.className;

    // Filter messages for the selected contact
    const filteredMessages = messages.filter(
        (msg) => msg.contactId === selectedContact
    );

    return (
        <div className={`chat-window ${currentTheme}`}>
            {selectedContact ? (
                <>
                    <div className="chat-header">
                        <p>Chat with {selectedContact.name || 'User'}</p>
                    </div>
                    <div className="messages-container">
                        {filteredMessages.length > 0 ? (
                            filteredMessages.map((msg) => (
                                <Message key={msg.id} message={msg} />
                            ))
                        ) : (
                            <p className="no-messages">No messages yet!</p>
                        )}
                    </div>
                    <MessageInput />
                </>
            ) : (
                <p className="select-contact">Please select a contact to start chatting.</p>
            )}
        </div>
    );
};

export default ChatWindow;
