import React, { useContext, useEffect, useState } from 'react';
import { GlobalContext } from '../context/GlobalContext';
import useIndexedDB from '../hooks/useIndexedDB';
import '../styles/contactList.css';

const ContactList = () => {
    const { contacts, theme, dispatch } = useContext(GlobalContext);
    const { getFromDB, addToDB, deleteFromDB } = useIndexedDB();
    const [newContactName, setNewContactName] = useState('');
    const [newContactPic, setNewContactPic] = useState('');
    const [selectedContact, setSelectedContact] = useState(null);
    const [showForm, setShowForm] = useState(false); // Toggle for add contact form

    useEffect(() => {
        const fetchContacts = async () => {
            try {
                const storedContacts = await getFromDB('contacts');
                if (storedContacts.length) {
                    dispatch({ type: 'SET_CONTACTS', payload: storedContacts });
                } else {
                    const contactData = [
                        { id: 1, name: 'Alice', pic: '' },
                        { id: 2, name: 'Bob', pic: '' },
                    ];
                    contactData.forEach((contact) => addToDB('contacts', contact));
                    dispatch({ type: 'SET_CONTACTS', payload: contactData });
                }
            } catch (error) {
                console.error('Error fetching contacts:', error);
            }
        };

        fetchContacts();
    }, [dispatch, getFromDB, addToDB]);

    // Add a new contact
    const handleAddContact = async () => {
        if (newContactName.trim()) {
            const newContact = {
                id: Date.now(),
                name: newContactName,
                pic: newContactPic || '', // Optional profile picture
            };
            await addToDB('contacts', newContact);
            dispatch({ type: 'ADD_CONTACT', payload: newContact });
            setNewContactName('');
            setNewContactPic('');
            setShowForm(false); // Close the form
        }
    };

    // Delete a contact
    const handleDeleteContact = async (id) => {
        await deleteFromDB('contacts', id);
        dispatch({ type: 'DELETE_CONTACT', payload: id });
    };

    // Toggle theme
    const toggleTheme = () => {
        const newTheme = theme === 'light' ? 'dark' : 'light';
        dispatch({ type: 'SET_THEME', payload: newTheme });
    };

    return (
        <div className={`contact-list ${theme}`}>
            <div className="contact-list-header">
                <h2>
                    Contacts
                    <span className="add-contact-icon" onClick={() => setShowForm(!showForm)}>
                        âž•
                    </span>
                </h2>
                <span className="theme-toggle" onClick={toggleTheme}>
                    ðŸŒ—
                </span>
            </div>

            {/* Add New Contact Form */}
            {showForm && (
                <div className="add-contact-form">
                    <input
                        type="text"
                        placeholder="Enter contact name"
                        value={newContactName}
                        onChange={(e) => setNewContactName(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Enter profile picture URL (optional)"
                        value={newContactPic}
                        onChange={(e) => setNewContactPic(e.target.value)}
                    />
                    <button onClick={handleAddContact}>Add Contact</button>
                </div>
            )}

            {/* Contact List */}
            {contacts.map((contact) => (
                <div
                    key={contact.id}
                    className={`contact-item ${selectedContact === contact.id ? 'selected' : ''}`}
                    onClick={() => setSelectedContact(contact.id)}
                >
                    <img
                        src={contact.pic || 'https://via.placeholder.com/50'} // Default profile pic
                        alt={contact.name}
                        className="contact-pic"
                    />
                    <div className="contact-info">
                        <span className="contact-name">{contact.name}</span>
                    </div>
                    <div className="contact-actions">
                        <button onClick={() => handleDeleteContact(contact.id)}>Delete</button>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default ContactList;
